import React, { useState, useEffect } from "react";
import axios from "axios";

import styles from "./Ballot.module.css";

import Race from "../Race/Race";

const Ballot = ({
  ballotID,
  ballotName,
  userID,
  raceOneID,
  raceTwoID,
  raceThreeID,
  user,
  deleteBallot,
  races,
}) => {
  const deleteRace = async (raceID) => {
    try {
      const response = await axios.delete(
        `http://localhost:3001/race/${raceID}`,
      );
      console.log(response.data.message);
    } catch (error) {
      console.error(
        "Error deleting race:",
        error.response?.data?.error || error.message,
      );
    }
  };

  const deleteBallotInDB = async (ballotID) => {
    try {
      const response = await axios.delete(
        `http://localhost:3001/ballot/${ballotID}`,
      );
      console.log(response.data.message);
    } catch (error) {
      console.error(
        "Error deleting ballot:",
        error.response?.data?.error || error.message,
      );
    }
  };

  const getRace = async (raceID) => {
    try {
      const response = await axios.get(`http://localhost:3001/race/${raceID}`);
      return response.data;
    } catch (error) {
      console.error(
        "Error getting race:",
        error.response?.data?.error || error.message,
      );
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (raceOneID != null) {
      await deleteRace(raceOneID);
    }

    if (raceTwoID != null) {
      await deleteRace(raceTwoID);
    }

    if (raceThreeID != null) {
      await deleteRace(raceThreeID);
    }

    await deleteBallotInDB(ballotID);
    deleteBallot(ballotID);
  };

  if (user) {
    return (
      <div className={styles.ballot}>
        <h1>{ballotName}</h1>
        {raceOneID != null && <Race />}
        {raceTwoID != null && <Race />}
        {raceThreeID != null && <Race />}
        {userID === Number(user.userID) && (
          <form onSubmit={handleSubmit}>
            <button type="submit">Delete</button>
          </form>
        )}
      </div>
    );
  }
};

export default Ballot;
