import React, { useState } from "react";

import styles from "./Race.module.css";

const Race = (updateVotes, race) => {
  const [selectedOption, setSelectedOption] = useState("");

  const handleChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const createVote = async (votePerson, userID, raceID) => {
    if (!votePerson || !userID || !raceID) {
      return null;
    }

    try {
      const response = await axios.post("http://localhost:3001/votes", {
        votePerson,
        userID,
        raceID,
      });
    } catch (err) {}
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`You voted for ${selectedOption}`);
    console.log("Selected option:", selectedOption);
    updateVotes();
  };

  return (
    <div className={styles.race}>
      <h2>{race.race.raceName}</h2>
      <form onSubmit={handleSubmit} className={styles.raceForm}>
        <div>
          <label>
            <input
              type="radio"
              value="Option1"
              checked={selectedOption === "Option1"}
              onChange={handleChange}
            />
            {race.race.racePersonOne} : {race.race.racePersonOneParty}
          </label>
        </div>
        <div>
          <label>
            <input
              type="radio"
              value="Option2"
              checked={selectedOption === "Option2"}
              onChange={handleChange}
            />
            {race.race.racePersonTwo} : {race.race.racePersonTwoParty}
          </label>
        </div>
        {race.race.racePersonThree !== "" && (
          <div>
            <label>
              <input
                type="radio"
                value="Option3"
                checked={selectedOption === "Option3"}
                onChange={handleChange}
              />
              {race.race.racePersonThree} : {race.race.racePersonThreeParty}
            </label>
          </div>
        )}
        <button type="sumbit">Submit</button>
      </form>
    </div>
  );
};

export default Race;
