import React, { useState } from "react";
import axios from "axios";

import styles from "./Race.module.css";

const Race = (setVotes, race) => {
  const [selectedOption, setSelectedOption] = useState("");

  const handleChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const createVote = async (votePerson, userID, raceID) => {
    if (!votePerson || !userID || !raceID) {
      return null;
    }

    try {
      const response = await axios.post("http://localhost:3001/votes", {
        votePerson,
        userID,
        raceID,
      });

      if (response.data.errors) {
        alert("Vote operation failed: " + response.data.error);
        return null;
      } else {
        alert("Vote operation successful");
        return response.data.id;
      }
    } catch (error) {
      console.log("Error:", error);
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let selectedPerson = "";
    alert(`You voted for ${selectedOption}`);
    switch (selectedOption) {
      case "Option1":
        selectedPerson = race.race.racePersonOne;
        break;
      case "Option2":
        selectedPerson = race.race.racePersonTwo;
        break;
      case "Option3":
        selectedPerson = race.race.racePersonThree;
        break;
      default:
        return null;
    }
    console.log("Selected option:", selectedOption);
    const voteResponse = await axios.get("http://localhost:3001/votes");
    setVotes(voteResponse.data);
  };

  return (
    <div className={styles.race}>
      <h2>{race.race.raceName}</h2>
      <form onSubmit={handleSubmit} className={styles.raceForm}>
        <div>
          <label>
            <input
              type="radio"
              value="Option1"
              checked={selectedOption === "Option1"}
              onChange={handleChange}
            />
            {race.race.racePersonOne} : {race.race.racePersonOneParty}
          </label>
        </div>
        <div>
          <label>
            <input
              type="radio"
              value="Option2"
              checked={selectedOption === "Option2"}
              onChange={handleChange}
            />
            {race.race.racePersonTwo} : {race.race.racePersonTwoParty}
          </label>
        </div>
        {race.race.racePersonThree !== "" && (
          <div>
            <label>
              <input
                type="radio"
                value="Option3"
                checked={selectedOption === "Option3"}
                onChange={handleChange}
              />
              {race.race.racePersonThree} : {race.race.racePersonThreeParty}
            </label>
          </div>
        )}
        <button type="sumbit">Submit</button>
      </form>
    </div>
  );
};

export default Race;
