import React from "react";

const RaceVotes = ({ race, votes }) => {
  console.log("Votes.jsx", votes);
  const voteCountMap = new Map();

  [race.racePersonOne, race.racePersonTwo, race.racePersonThree].forEach(
    (person) => {
      voteCountMap.set(person, 0);
    },
  );

  votes.forEach((vote) => {
    if (vote.raceID === race.raceID) {
      switch (vote.votePerson) {
        case "Option1":
          voteCountMap.set(
            race.racePersonOne,
            voteCountMap.get(race.racePersonOne) + 1,
          );
          break;
        case "Option2":
          voteCountMap.set(
            race.racePersonTwo,
            voteCountMap.get(race.racePersonTwo) + 1,
          );
          break;
        case "Option3":
          voteCountMap.set(
            race.racePersonThree,
            voteCountMap.get(race.racePersonThree) + 1,
          );
          break;
        default:
          break;
      }
    }
  });

  return (
    <div>
      <h1>{race.raceName} Votes</h1>
      <div>
        {Array.from(voteCountMap.keys())
          .filter((person) => person !== "")
          .map((person) => (
            <p key={person}>
              {person}: {voteCountMap.get(person)}
            </p>
          ))}
      </div>
    </div>
  );
};

export default RaceVotes;
