import React, { useState } from "react";

import styles from "./PollsBox.module.css";

const PollsBox = () => {
  const [selectedOption, setSelectedOption] = useState("");

  const handleChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault("Selected option:", selectedOption);
  };
  return (
    <div className={styles.container}>
      <div className={styles.ballot}>
        <p>Ballot Name</p>
        <div className={styles.race}>
          <p>Race Name</p>
          <form onSubmit={handleSubmit} className={styles.raceForm}>
            <div>
              <label>
                <input
                  type="radio"
                  value="Option1"
                  checked={selectedOption === "Option1"}
                  onChange={handleChange}
                />
                Person Name : Person Party
              </label>
            </div>
          </form>
        </div>
        <div className={styles.race}></div>
        <div className={styles.race}></div>
      </div>
    </div>
  );
};

export default PollsBox;
